mapper.select:
select user_no, user_id, user_name, user_password, user_phone
from user

    
        where user_id like concat('%', #{word}, '%')
    
    
        where ${key} = #{word}
    

order by user_no
limit #{start}, #{spp}

select count(user_no) from user
where user_id like concat('%', #{word}, '%')
where ${key} = #{word}

mapper.update:
update user
set user_name = #{user_name}, user_password = #{user_password}, user_phone = #{user_phone}
where user_no = #{user_no}
